            {{ 'sylius.email.user.account_verification.greeting'|trans({}, null, localeCode) }}
            <strong>{{ user.username }}</strong><br>
        </div>
        {{ 'sylius.email.user.account_verification.strategy'|trans({}, null, localeCode) }}:
    </div>

    <div style=\"text-align: center;\">
        <a href=\"{{ url|raw }}\"
